import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { checkToDo } from "../actions/checkToDo";

class TaskList extends Component {
  constructor(props) {
    checkToDo;
    super(props);
  }

  render() {
    if (this.props.task) {
      return (
        <div>
          <ul>
            {this.props.task
              .filter(item => item.status == "N")
              .map(task => (
                <li key={task.id}>
                  <input
                    type="checkbox"
                    checked={false}
                    value={task.id}
                    onChange={() => this.props.checkToDo(task)}
                  />
                  <button
                    className="DeleteBtn"
                    onClick={() => this.props.delTask(task.id)}
                  >
                    X
                  </button>{" "}
                  {task.text}
                </li>
              ))}
          </ul>
          <style jsx>
            {
              "ul {list-style:none;} .DeleteBtn {background-color: red; color: white;}"
            }
          </style>
        </div>
      );
    } else {
      return <p>no item</p>;
    }
  }
}

const mapStateToProps = state => {
  return {
    item: state.todo
  };
};

const mapDispatchToProps = dispatch => {
  return bindActionCreators({ checkToDo: checkToDo }, dispatch);
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(TaskList);
