import React, {Component} from 'react'
import {connect} from 'react-redux'
import { bindActionCreators } from 'redux';
import UserList from '../containers/user-list'
import UserDetail from '../containers/user-detail'
import TaskList from '../containers/taskList'
import DoneList from '../containers/DoneList'
import { throws } from 'assert';

class todolist extends Component{
    constructor(props){
        super(props);

        this.state = {
            tempDue: '',
            tempText: '',
            tempStatus: 'N',
            task: [
                {
                    id: 1,
                    due: 'no due date',
                    text: 'do this',
                    status: 'N'
                },
                {
                    id: 2,
                    due: 'no due date',
                    text: 'do that thing',
                    status: 'Y'
                },
                {
                    id: 3,
                    due: 'april 1st',
                    text: 'do them',
                    status: 'N'
                }
            ]
        }

        this.updateInput = this.updateInput.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    updateInput(event){
        this.setState({
            tempText: event.target.value
        })
    }

    handleSubmit(){
        if(this.state.tempText != ''){ //tempDue != '' && tempText != ''
            this.setState({
                newtask: {
                    tempDue: '',
                    tempText: '',
                    tempStatus: 'N'
                },
                task: task.concat([
                    {
                        id: this.state.task.length + 1,
                        due: 'no due date',
                        text: this.state.tempText,
                        status: 'N'
                    }
                ])
            })
        }
        else{
            alert('Please input task first');
        }
    }

    render(){
        return(
        <div>
            <div>
                <input id="taskInput" type="text" placeholder="insert task..." onChange={this.updateInput}/>
                <button onClick={() => this.handleSubmit}>Add</button>
            </div>
            <div>
                <h2>No Due Date</h2>
                <TaskList task={this.state.task}/>
            </div>
            <div>
                <h2>Completed</h2>
                <DoneList />
            </div>
        </div>
        );
    }
}

export default todolist

