import dueDateFn from "../reducers/reducer-duedate-data";

const dueDate = dueDateFn();

export default function(state = dueDate, action) {
  let newState;
  console.log("def state duedate", state);
  switch (action.type) {
    case "ADD_DUE":
      if (state.filter(item => item.due == action.payload.due).length < 1) {
        newState = state.concat(action.payload);
        console.log("ADD_DUE newState duedate", newState);
        return newState;
      }

    case "DELETE_DUE":
      newState = state.filter(item => item.due != action.payload.due);
      console.log("DELETE_DUE newState duedate", newState);
      return newState;
  }
  return state;
}
