import DefaultStateFn from "../reducers/reducer-todolist";

const defaultState = DefaultStateFn();

export default function(state = defaultState, action) {
  console.log("state sblm switch ", state, "action", action);
  let newState;
  switch (action.type) {
    case "TASK_CHECKED":
      console.log("TASK_CHECKED");
      // state = {
      //     ...state,
      //     status: 'Y'
      // };

      //   newState = state;
      //   newState[action.payload].status = "Y";

      newState = state.map((s, i) =>
        i === action.payload ? { ...s, status: "Y" } : s
      );
      console.log("newState", newState);
      return newState;

    case "TASK_UNCHECKED":
      console.log("TASK_UNCHECKED");
      newState = state.map((s, i) =>
        i === action.payload ? { ...s, status: "N" } : s
      );
      console.log("newState", newState);
      return newState;
  }
  return state;
}
