import DefaultStateFn from '../reducers/reducer-todolist'

const defaultState = DefaultStateFn()

export default function(state=defaultState, action){
    console.log('state sblm switch ', state);
    switch(action.type){
        case 'TASK_CHECKED':
            console.log('TASK_CHECKED');
            // state = {
            //     ...state,
            //     status: 'Y'
            // };
            const id=action.payload
            const newState = state.map((s,i)=>i===id?{...s, status: 'N'}:s)
            break;
        case 'TASK_UNCHECKED':
            console.log('TASK_UNCHECKED');
            state = {
                ...state,
                status: 'N'
            };
            break;
    }
    console.log('state setelah switch ', state);
    return state;
}
