import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import UserList from "../containers/user-list";
import UserDetail from "../containers/user-detail";
import TaskList from "../containers/taskList";
import DoneList from "../containers/DoneList";
import taskChecked from "../reducers/reducer-task-checked";
import { addTask } from "../actions/addTask";
import { deleteTask } from "../actions/deleteTask";

class todolist extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tempDue: "",
      tempText: "",
      tempStatus: "N"
    };
  }

  updateInput = event => {
    this.setState({
      tempText: event.target.value
    });
  };

  handleSubmit = () => {
    if (this.state.tempText != "") {
      this.setState({
        tempDue: "",
        tempText: "",
        tempStatus: "N"
        /*
        task: this.state.task.concat([
          {
            id: this.state.task.length + 1,
            due: "no due date",
            text: this.state.tempText,
            status: this.state.tempStatus
          }
        ])*/
      });
      let task = {
        id: this.state.task.length + 1,
        due: "no due date",
        text: this.state.tempText,
        status: this.state.tempStatus
      };
    } else {
      alert("Please input task first");
    }
  };

  handleDelete = id => {
    /*
    let new_list = this.state.task.filter(item => item.id != id);
    this.setState({
      task: new_list
    });*/
  };

  render() {
    console.log(this.state.task);
    return (
      <div>
        <div>
          <input
            id="taskInput"
            type="text"
            placeholder="insert task..."
            value={this.state.tempText}
            onChange={this.updateInput}
          />
          <button onClick={this.handleSubmit}>Add</button>
        </div>
        <div>
          <h2>No Due Date</h2>
          <TaskList task={this.props.taskChecked} delTask={this.handleDelete} />
        </div>
        <div>
          <h2>Completed</h2>
          <DoneList task={this.props.taskChecked} delTask={this.handleDelete} />
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    todo: state.todo,
    taskChecked: state.taskChecked
  };
};

export default connect(mapStateToProps)(todolist);

