import React, {Component} from 'react'
import {connect} from 'react-redux'
import { bindActionCreators } from 'redux';
import UserList from '../containers/user-list'
import UserDetail from '../containers/user-detail'
import TaskList from '../containers/taskList'
import DoneList from '../containers/DoneList'
import { throws } from 'assert';

class todolist extends Component{
    constructor(props){
        super(props);
    }

    state = {
        newtask: {
            due: '',
            text: '',
            status: 'N'
        },
        task: [
            {
                id: 1,
                due: 'no due date',
                text: 'do this',
                status: 'N'
            },
            {
                id: 2,
                due: 'no due date',
                text: 'do that thing',
                status: 'Y'
            },
            {
                id: 3,
                due: 'april 1st',
                text: 'do them',
                status: 'N'
            }
        ]
    }

    updateInput(event){
        this.setState({
            newtask: {text: event.target.value}
        })
    }

    handleSubmit(){
        if(this.state.newtask.text != ''){ //due != '' && text != ''
            this.setState({
                newtask: {
                    due: '',
                    text: '',
                    status: 'N'
                },
                task: task.concat([
                    {
                        id: this.state.task.length + 1,
                        due: 'no due date',
                        text: this.state.newtask.text,
                        status: 'N'
                    }
                ])
            })
        }
        else{
            alert('Please input task first');
        }
    }

    render(){
        return(
        <div>
            <div>
                <input id="taskInput" type="text" placeholder="insert task..." onChange={this.updateInput}/>
                <button onClick={() => this.handleSubmit}>Add</button>
            </div>
            <div>
                <h2>No Due Date</h2>
                <TaskList task={this.state.task}/>
            </div>
            <div>
                <h2>Completed</h2>
                <DoneList />
            </div>
        </div>
        );
    }
}

export default todolist

